select user_id, user_password, user_name, phone_number, join_date
from users
where userid = #{userId}

select token
from users
where user_id = #{userId}

mapper.update:
update users
set token = #{token}
where user_id = #{userId}

update users
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}

mapper.select:
